package com.xmmxjy.cms.entity;import java.io.Serializable;import java.util.Date;import java.math.BigDecimal;import javax.persistence.Id;import javax.persistence.Table;/** * 描述：栏目 * @author: xmm * @since：2016年12月09日 13时36分11秒 星期五  * @version:1.0 */@Table(name = "rh_channel")public class ChannelEntity implements Serializable{	private static final long serialVersionUID = 1L;		/**	 *主键	 */	@Id	private Integer id;	/**	 *父栏目ID	 */	private Integer parentId;	/**	 *栏目名称	 */	private String name;	/**	 *类型	 */	private Integer type;	/**	 *顺序	 */	private Integer sort;	/**	 *是否显示	 */	private Integer isDisplay;	public Integer getId() {	    return this.id;	}	public void setId(Integer id) {	    this.id=id;	}	public Integer getParentId() {	    return this.parentId;	}	public void setParentId(Integer parentId) {	    this.parentId=parentId;	}	public String getName() {	    return this.name;	}	public void setName(String name) {	    this.name=name;	}	public Integer getType() {	    return this.type;	}	public void setType(Integer type) {	    this.type=type;	}	public Integer getSort() {	    return this.sort;	}	public void setSort(Integer sort) {	    this.sort=sort;	}	public Integer getIsDisplay() {	    return this.isDisplay;	}	public void setIsDisplay(Integer isDisplay) {	    this.isDisplay=isDisplay;	}}