package com.xmmxjy.system.entity;import org.springframework.format.annotation.DateTimeFormat;import javax.persistence.Id;import javax.persistence.Table;import java.io.Serializable;import java.util.Date;/** * 描述：后台用户 * @author: xmm * @since：2016年09月19日 15时49分12秒 星期一  * @version:1.0 */@Table(name = "rh_user")public class UserEntity implements Serializable{	private static final long serialVersionUID = 1L;		/**	 *主键,UUID自动生成	 */	@Id	private String id;	/**	 *用户名	 */	private String username;	/**	 *盐值	 */	private String salt;	/**	 *密码	 */	private String password;	/**	 *真实姓名	 */	private String realName;	/**	 *用户状态	 */	private Integer status;	/**	 *邮箱	 */	private String email;	/**	 *生日	 */	@DateTimeFormat(pattern = "yyyy-MM-dd")	private Date birthday;	/**	 *性别	 */	private String sex;	/**	 *手机号码	 */	private String mobilePhone;	/**	 *办公室电话	 */	private String officePhone;	/**	 *所属区域	 */	private String region;	/**	 *上次登录时间	 */	private String lastLoginTime;	/**	 *上次登录IP	 */	private String lastLoginIp;	/**	 *角色ID	 */	private String roleId;	/**	 *部门ID	 */	private String departId;	/**	 *修改人	 */	private String updateName;	/**	 *修改时间	 */	@DateTimeFormat(pattern = "yyyy-MM-dd HH:mm:ss")	private Date updateDate;	/**	 *修改人id	 */	private String updateBy;	/**	 *创建人	 */	private String createName;	/**	 *创建时间	 */	private Date createDate;	/**	 *创建人id	 */	private String createBy;	public String getId() {	    return this.id;	}	public void setId(String id) {	    this.id=id;	}	public String getUsername() {	    return this.username;	}	public void setUsername(String username) {	    this.username=username;	}	public String getSalt() {	    return this.salt;	}	public void setSalt(String salt) {	    this.salt=salt;	}	public String getPassword() {	    return this.password;	}	public void setPassword(String password) {	    this.password=password;	}	public String getRealName() {	    return this.realName;	}	public void setRealName(String realName) {	    this.realName=realName;	}	public Integer getStatus() {	    return this.status;	}	public void setStatus(Integer status) {	    this.status=status;	}	public String getEmail() {	    return this.email;	}	public void setEmail(String email) {	    this.email=email;	}	public Date getBirthday() {	    return this.birthday;	}	public void setBirthday(Date birthday) {	    this.birthday=birthday;	}	public String getSex() {	    return this.sex;	}	public void setSex(String sex) {	    this.sex=sex;	}	public String getMobilePhone() {	    return this.mobilePhone;	}	public void setMobilePhone(String mobilePhone) {	    this.mobilePhone=mobilePhone;	}	public String getOfficePhone() {	    return this.officePhone;	}	public void setOfficePhone(String officePhone) {	    this.officePhone=officePhone;	}	public String getRegion() {	    return this.region;	}	public void setRegion(String region) {	    this.region=region;	}	public String getLastLoginTime() {	    return this.lastLoginTime;	}	public void setLastLoginTime(String lastLoginTime) {	    this.lastLoginTime=lastLoginTime;	}	public String getLastLoginIp() {	    return this.lastLoginIp;	}	public void setLastLoginIp(String lastLoginIp) {	    this.lastLoginIp=lastLoginIp;	}	public String getRoleId() {	    return this.roleId;	}	public void setRoleId(String roleId) {	    this.roleId=roleId;	}	public String getDepartId() {	    return this.departId;	}	public void setDepartId(String departId) {	    this.departId=departId;	}	public String getUpdateName() {	    return this.updateName;	}	public void setUpdateName(String updateName) {	    this.updateName=updateName;	}	public Date getUpdateDate() {	    return this.updateDate;	}	public void setUpdateDate(Date updateDate) {	    this.updateDate=updateDate;	}	public String getUpdateBy() {	    return this.updateBy;	}	public void setUpdateBy(String updateBy) {	    this.updateBy=updateBy;	}	public String getCreateName() {	    return this.createName;	}	public void setCreateName(String createName) {	    this.createName=createName;	}	public Date getCreateDate() {	    return this.createDate;	}	public void setCreateDate(Date createDate) {	    this.createDate=createDate;	}	public String getCreateBy() {	    return this.createBy;	}	public void setCreateBy(String createBy) {	    this.createBy=createBy;	}}